function col_in_tilemap (col: number) {
    return col >= 0 && col < tiles.tilemapColumns()
}
scene.onOverlapTile(SpriteKind.Player, myTiles.tile4, function (sprite, location) {
    timer.throttle("win", 500, function () {
        tiles.placeOnTile(sprite, location)
        timer.after(20, function () {
            game.over(true)
        })
    })
})
function path_left () {
    current_col += -2
    if (tiles.tileIs(tiles.getTileLocation(current_col, current_row), myTiles.tile3)) {
        tiles.setTileAt(tiles.getTileLocation(current_col + 1, current_row), myTiles.tile1)
        tiles.setTileAt(tiles.getTileLocation(current_col, current_row), myTiles.tile1)
    }
}
function path_down () {
    current_row += 2
    if (tiles.tileIs(tiles.getTileLocation(current_col, current_row), myTiles.tile3)) {
        tiles.setTileAt(tiles.getTileLocation(current_col, current_row - 1), myTiles.tile1)
        tiles.setTileAt(tiles.getTileLocation(current_col, current_row), myTiles.tile1)
    }
}
